document.body.appendChild(function(){const n=document.createElement("div");if(console.log("location.pathname:",location.pathname),"/"===location.pathname)n.innerHTML="hello world";else if(location.pathname.startsWith("/about/")){const e=location.pathname.slice(7);n.innerHTML="loading...",fetch("/api/info?name="+e).then((n=>n.json())).then((e=>{e.error?n.innerHTML=e.error.message:n.innerHTML=`${e.name}, come from ${e.from}`}))}else n.innerHTML="not found";return n}());